import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit{

  constructor() {}

  signupForm:FormGroup;
  msg:string = "";

  ngOnInit(): void {
    this.signupForm = new FormGroup({
      username:new FormControl(""),
      fullName:new FormControl(""),
      address:new FormGroup({
        street:new FormControl(""),
        city:new FormControl(""),
        state:new FormControl(""),
        country:new FormControl(""),
        pincode:new FormControl("")
      }),
      emailid:new FormControl("", [Validators.required, Validators.email]),
      password:new FormControl("", [Validators.required]),
      img:new FormControl(""),
      contact:new FormControl("")
    })
  }

  signUp() {
    let user = this.signupForm.value;
    console.log(user);
  }

}
<form class="login" [formGroup]="signupForm" (ngSubmit)="signUp()">
    <fieldset class="title">Login</fieldset>
    <div>
      <label for="inputEmail3" class="col-sm-2 col-form-label email">Email</label>
      <div class="col-sm-10">
        <input type="email" class="form-control email-input" id="inputEmail4" placeholder="abc@example.com" formControlName="emailid">

        <div *ngIf="signupForm.controls['email']?.invalid && signupForm.controls['email']?.touched">
        <span class="error" [hidden]="!signupForm.controls['email'].hasError('required')">Email is required</span>
        <span class="error" [hidden]="!signupForm.controls['email'].hasError('email')">Invalid Email</span>
        </div>
      </div>
    </div>
    <div>
      <label for="inputPassword3" class="col-sm-2 col-form-label password">Password</label>
      <div class="col-sm-10">
        <input type="password" class="form-control password-input" id="inputPassword3" placeholder="********" formControlName="password">

        <div *ngIf="signupForm.controls['password']?.invalid && signupForm.controls['password']?.touched">
        <span class="error" [hidden]="!signupForm.controls['password'].hasError('required')">Password Required</span>
        <span class="error" [hidden]="!signupForm.controls['password'].hasError('minlength')">Password Not Valid</span>
        </div>
      </div>
    </div>
    <div class="check-box">
        <input type="checkbox" class="form-check-input">
        <label for="" class="fomr-check-label">Remember Me</label>
    </div>
    <button type="submit" class="btn btn-primary">SignUp</button><br>
    <span class="error-msg">{{msg}}</span>
    <br>
    <a routerLink="/login">Back To Login</a>
</form>
