    //       next:(result:any) => {
    //         console.log(result);
    //       }, error:(error:any) => {
    //         console.log("Error");
    //       }, complete:() => {
    //         this.orderService.loadAllOrders();
    //         console.log(id);
    //         console.log(`Product ${id} deleted`);
    //       }
    //     })
    //   }else{
    //     alert("Product didn\'t delete")
    //   }
    // }
}
<div class="all">
    <h3>Orders</h3>
    your date details {{dateInfo}} <br>
    your date details {{dateInfo | date}}
    <table class="table" >
        <tr>
            <th>ID</th>
            <th>Order Date</th>
            <th>Order Status</th>
            <th>Shipment Charges</th>
            <th>Items</th>
            <th>Total Items</th>
            <th>Total</th>
            <th></th>
        </tr>
        <tr *ngFor="let order of orders; let i = index">
            <td>{{order.orderid}}</td>
            <td>{{order.orderDate}}</td>

            <td>
                <input class="btn btn-primary btn-width" type="button" value="Edit Order">
            </td>
        </tr>
    </table>
    </div>
